package Assignment;

public class TriangleMulti {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int k=1;
        for(int i=0;i<8;i++)
        {
        	for(int j=0;j<=i;j++)
        	{
        		System.out.print(k*j);
        	}
        	System.out.println();
        	k++;
        		
        }
	}

}
